<?php/** * Created by PhpStorm. * User: 陈祥 * Date: 2016/5/30 * Time: 10:57 */namespace App\Helpers;class UrlParmToArray{	public static function StrToArr($ParmStr){		$Arr = [];		$Arrkey = explode('&',$ParmStr );		if(count($Arrkey)>0){			foreach ($Arrkey as $key => $value){				$valArr = explode('=',$value );				if(count($valArr)>1){					$temp[$valArr[0]] = $valArr[1];				}else{					$temp[$valArr[0]] = '';				}				$Arr[] = $temp;      }		}		return $Arr;	}}